mapper.select:
select question_Farm_No as questionFarmNo, question_Farm_Title as questionFarmTitle, question_Farm_Content as questionFarmContent, question_Farm_Regdate as questionFarmRegdate, farm_No as farmNo, question_Farm_End_YN as questionFarmEndYN, farm_Name as farmName, question_Farm_Answer_YN as questionFarmAnswerYN from (select row_number() over (order by question_farm_no desc) as num, question_farm.*,farm_name from question_farm left join farm on(question_farm.farm_no = farm.farm_no) where question_farm_end_yn='N'
and question_farm_title like '%'||#{keyword}||'%'
and farm_name like '%'||#{keyword}||'%'
and question_farm_content like '%'||#{keyword}||'%'
and (question_farm_title like '%'||#{keyword}||'%' or question_farm_content like '%'||#{keyword}||'%')
) where num between #{start} and #{end}

select question_User_No as questionUserNo, question_User_Title as questionUserTitle, question_User_Content as questionUserContent, question_User_Regdate as questionUserRegdate, user_No as userNo, question_User_End_YN as questionUserEndYN, questionphoto_No as questionphotoNo, question_User_Code as questionUserCode, question_User_Classify as questionUserClassify, farm_No as farmNo, order_No as orderNo, user_Nick as userNick, question_User_Answer_YN as questionUserAnswerYN from (select row_number() over (order by question_user_no desc) as num, question_user.*, member.user_nick from question_user left join member on(question_user.user_no = member.user_no) where question_user_end_yn='N' and question_user_classify='A'
and question_user_title like '%'||#{keyword}||'%'
and user_nick like '%'||#{keyword}||'%'
and question_user_content like '%'||#{keyword}||'%'
and (question_user_title like '%'||#{keyword}||'%' or question_user_content like '%'||#{keyword}||'%')
) where num between #{start} and #{end}

SELECT user_no as userNo, user_id as userId, user_pwd as userPwd, user_name as userName, user_nick as userNick, user_email as userEmail, user_phone as userPhone, user_address_main as userAddressMain, user_address_sub as userAddressSub, user_enrolldate as userEnrollDate, user_end_yn as userEndYN, rating as rating FROM (select row_number() over (order by user_no desc) as num,member.* from member
where user_no = #{keyword}
where user_name like '%'||#{keyword}||'%'
where user_nick like '%'||#{keyword}||'%'
where user_nick like '%'||#{keyword}||'%' or user_name like '%'||#{keyword}||'%'
) where num between #{start} and #{end}

select refund_No as refundNo, buy_No as buyNo, user_No as userNo, farm_No as farmNo, farm_YN as farmYN, admin_YN as adminYN, del_YN as delYN, question_User_No as questionUserNo, order_no as orderNo from (select row_number() over (order by refund_no desc) as num,refund.*,purchaselist.order_no from refund left join purchaselist on (refund.buy_no = purchaselist.buy_no) where refund.farm_yn='Y'
and refund.user_no like '%'||#{keyword}||'%'
and refund.farm_no like '%'||#{keyword}||'%'
and refund.buy_no like '%'||#{keyword}||'%'
and purchaselist.order_no like '%'||#{keyword}||'%'
) where num between #{start} and #{end}
