mapper.update:

    SELECT QUESTIONPHOTO_NO FROM QuestionPhoto WHERE QUESTIONPHOTO_UPDATETIME=#{questionPhotoUpdateTime}

update		QuestionPhoto
set			QUESTIONPHOTO_ORIGINAL=#{questionPhotoOriginal},
			QUESTIONPHOTO_CHANGE=#{questionPhotoChange},
			QUESTIONPHOTO_FILEPATH=#{questionPhotoFilePath},
			QUESTIONPHOTO_FILESIZE=#{questionPhotoFileSize},
			QUESTIONPHOTO_UPDATETIME=#{questionPhotoUpdateTime}
where		QUESTIONPHOTO_NO=#{QuestionPhotoNo}

mapper.select:
select QUESTION_USER_NO as QuestionUserNo, QUESTION_USER_TITLE as QuestionUserTitle, QUESTION_USER_CONTENT as QuestionUserContent, QUESTION_USER_REGDATE as QuestionUserRegdate, USER_NO as UserNo, user_nick as userNick, QUESTION_USER_END_YN as QuestionUserEndYN, QUESTIONPHOTO_NO as QuestionPhotoNo, QUESTION_USER_CODE as QuestionUserCode, QUESTION_USER_CLASSIFY as QuestionUserClassify, FARM_NO as FarmNo, ORDER_NO as OrderNo, QUESTION_USER_ANSWER_YN as QuestionUserAnswer from (select row_number() over(order by Question_user_no desc)as num, question_user.* ,user_nick from question_user left join member on (question_user.user_no=member.user_no) where QUESTION_USER_END_YN='N' and
question_user.user_no=#{member.userNo}
question_user.farm_no=#{farm.farmNo}
and QUESTION_USER_TITLE like '%'||#{keyword}||'%'
and QUESTION_USER_content like '%'||#{keyword}||'%'
and (QUESTION_USER_TITLE like '%'||#{keyword}||'%' or QUESTION_USER_content like '%'||#{keyword}||'%')
) where num between #{start} and #{end}

select count(*) from question_user where 


			question_user.user_no=#{member.userNo} 


			question_user.farm_no=#{farm.farmNo} 




		and QUESTION_USER_TITLE like '%'||#{keyword}||'%'


		and QUESTION_USER_content like '%'||#{keyword}||'%'


		and (QUESTION_USER_TITLE like '%'||#{keyword}||'%' or QUESTION_USER_content like '%'||#{keyword}||'%')

mapper.update:
