mapper.select:
select QUESTION_FARM_NO as questionFarmNo, QUESTION_FARM_TITLE as questionFarmTitle, QUESTION_FARM_CONTENT as questionFarmContent, QUESTION_FARM_REGDATE as questionFarmRegdate, FARM_NO as farmNo, QUESTION_FARM_END_YN as questionFarmEndYN, QUESTION_FARM_ANSWER_YN AS questionFarmAnswerYN from (select row_number() over(order by QUESTION_FARM_NO desc)as num, QUESTION_FARM.* from QUESTION_FARM left join farm on (QUESTION_FARM.FARM_NO = farm.FARM_NO) where QUESTION_FARM_END_YN='N'
and QUESTION_FARM_TITLE like '%'||#{keyword}||'%'
and QUESTION_FARM_CONTENT like '%'||#{keyword}||'%'
and (QUESTION_FARM_TITLE like '%'||#{keyword}||'%' or QUESTION_FARM_CONTENT like '%'||#{keyword}||'%')
) where num between #{start} and #{end}

select 		question_Answer_Content as questionAnswerContent 
from 		question_answer 
where 		question_board_code='F' and question_board_no=#{_parameter}

mapper.update:
update			QUESTION_FARM
set				QUESTION_FARM_TITLE = #{questionFarmTitle},
				QUESTION_FARM_CONTENT = #{questionFarmContent}
where			QUESTION_FARM_NO = #{questionFarmNo}

update			QUESTION_FARM
set				QUESTION_FARM_END_YN = 'Y'
where			QUESTION_FARM_NO = #{_parameter}
